    { label: "Performance", color: '#B24C63'},
    { label: "Readability", color: '#A77E58'},
    { label: "Reliability", color: '#001D4A'},
    { label: "Security", color: '#CA3C25'},
  ];

  const { data: reportDatas, isSuccess } = useQuery(['report', reportId], () => getReportData(reportId));

  // summary table
  const summaryTable = [];
